dcui.Function:
if (this.Params.Id) 
	e.Data.Id = this.Params.Id;
	
// if names changed
if (e.Data.Names) {
	e.Data.Names = e.Data.Names.split(',');
	
	for (var i = 0; i < e.Data.Names.length; i++)
		e.Data.Names[i] = e.Data.Names[i].trim();
}

e.Message = { 
	Service: 'dcCoreDataServices',
	Feature: 'Domains',
	Op: 'MyUpdateDomain',
	Body: e.Data
};

			dc.comm.sendMessage({ 
				Service: 'dcmCms',
				Feature: 'Site',
				Op: 'BuildMap'
			}, function(rmsg) {
				if (rmsg.Result > 0) {
					dc.pui.Popup.alert('Unable to run BuildMap: ' + rmsg.Message);
					return;
				}

				dc.pui.Popup.alert('Completed!');
			});

			dc.comm.sendMessage({ 
				Service: 'dcmCms',
				Feature: 'Site',
				Op: 'ImportSite'
			}, function(rmsg) {
				if (rmsg.Result > 0) {
					dc.pui.Popup.alert('Unable to run ImportSite: ' + rmsg.Message);
					return;
				}

				dc.pui.Popup.alert('Completed!');
			});

			dc.comm.sendMessage({ 
				Service: 'dcCoreDataServices',
				Feature: 'Database',
				Op: 'ExecuteProc', 
				Body: { Proc: 'dcReindexDomain' }
			}, function(rmsg) {
				if (rmsg.Result > 0) {
					dc.pui.Popup.alert('Unable to reindex tables: ' + rmsg.Message);
					return;
				}

				dc.pui.Popup.alert('Completed!');
			});
